$(document).on('turbolinks:load', function() {
  $(function(){
    var swiper = new Swiper('.swiper-container', {
    navigation: {
    nextButton: '.swiper-button-next',
    prevButton: '.swiper-button-prev',
  },
    pagination:  {
      el: '.swiper-pagination',
    },
    loop: true,
    autoplay: {
      delay: 3000,
      disapbleOnInteraction: true
    },
   });
  });
})
